var s=document.querySelectorAll('[data-toggle="modal"]');s.forEach(t=>{t.addEventListener("click",e=>{var d=t.getAttribute("data-target"),a=document.querySelector(d);a.classList.remove("hidden"),setTimeout(()=>{a.classList.add("show")},10),e.stopPropagation(),e.preventDefault()})});var o=document.querySelectorAll('[data-dismiss="modal"]');o.forEach(t=>{t.addEventListener("click",()=>{var e=t.closest(".modal");e.classList.remove("show")})});var i=document.querySelectorAll(".modal");i.forEach(t=>{t.classList.contains("hidden")||t.classList.add("hidden"),t.addEventListener("transitionend",()=>{var e=window.getComputedStyle(t);e.opacity==="0"?(t.classList.add("hidden"),Livewire.emit("modal.closed")):t.classList.remove("hidden")})});window.addEventListener("click",t=>{var e=document.querySelector(".modal.show");t.target===e&&e.classList.remove("show")});
